package week2.day2;

import org.apache.poi.util.SystemOutLogger;

public class Learnforloopwithif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<=10;i++) {
			if(i>5)  {
				System.out.println("welcome"+i);}
			else {
				System.out.println("hearty welcome"+i);}
		}
		System.out.println("*********************");
		for (int i=0;i<=10;i++) {
			if(i==5) {
				System.out.println("sunflower"+i);}
			else {
				System.out.println("jasmine"+i);}
		}
		System.out.println("***************");
		for(int i=0; i<=10;i++) {
			if(i==5) {
				System.out.println("trees"+i);}
			else {
				System.out.println("seeds"+i);}
		}
		System.out.println("************");
		for(int i=0;i<=10;i++) {
			if(i==4) {
				System.out.println("water"+i);
			    break; }
			else {
				System.out.println("soil"+i);}
		}
		System.out.println("**************");
		for(int i=0; i<=10;i++) {
			if(i==4) {
				System.out.println("coffee"+i);
				continue;}
			else {
				System.out.println("tea"+i);
			}
		}
			
			
		}
			
				
				
			}
		
		
				
				
			
			
				
			
		
			
	
			
			
				
			
		
		

	


